import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./Grades.css";

const Grades = () => {
  const [grades, setGrades] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem("user"));

  useEffect(() => {
    if (!user || !user.is_student) {
      navigate("/");
      return;
    }
    const token = localStorage.getItem("token");
    if (!token) {
      setError("Please log in to view grades");
      setLoading(false);
      return;
    }

    fetch("http://localhost:5000/api/students/my-grades", {
      headers: { "Authorization": `Bearer ${token}` }
    })
      .then(res => {
        if (!res.ok) throw new Error(`HTTP error! Status: ${res.status}`);
        return res.json();
      })
      .then(data => {
        setGrades(data);
        setLoading(false);
      })
      .catch(err => {
        console.error("Fetch error:", err);
        setError(`Failed to load grades: ${err.message}`);
        setLoading(false);
      });
  }, [navigate, user]);

  if (loading) return <div>Loading grades...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="grades-container">
      <h1>My Grades</h1>
      {grades.length > 0 ? (
        <ul>
          {grades.map(g => (
            <li key={g.id}>
              Course: {g.course?.name || "Unknown"} (ID: {g.course_id}) - Grade: {g.grade}
            </li>
          ))}
        </ul>
      ) : (
        <p>No grades available.</p>
      )}
    </div>
  );
};

export default Grades;
