import React from 'react';
import { Link } from "react-router-dom";
import './Home.css'
import hero_img from '../../assets/hero.jpeg'

const Home = () => {
  return (
    <div className='home'>
     <div className='home-image'>
<img src={home_img} alt="" className='home-logo'/>
   </div>
    <div className='home-content'>
      <h1 className='home-header'>Welcome to Bloom Academy</h1>
      <p className='home-description'>An online learning platform to boost your skills</p>
      
      <Link to="/courses" className='explore-link' > <button  className="explore-btn">Explore Courses</button></Link>
  
  
      
      
    </div>
    </div>
  );
};

export default Home;
