import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import './AddCourses.css'

const AddCourse = ({ courses, setCourses }) => {
  // Course fields
  const [courseTitle, setCourseTitle] = useState("");
  const [courseDescription, setCourseDescription] = useState("");
  const [thumbnail, setThumbnail] = useState(null);

  // Lecture state
  const [lectures, setLectures] = useState([]);
  const [showLectureModal, setShowLectureModal] = useState(false);

  // Fields in the lecture modal
  const [lectureTitle, setLectureTitle] = useState("");
  const [lectureDuration, setLectureDuration] = useState("");
  const [lectureUrl, setLectureUrl] = useState("");
  const [lecturePreviewFree, setLecturePreviewFree] = useState(false);

  const navigate = useNavigate();

  // Add a single lecture to the course's lecture list
  const handleAddLecture = () => {
    if (!lectureTitle || !lectureDuration || !lectureUrl) {
      alert("Please fill in all required lecture fields!");
      return;
    }

    const newLecture = {
      id: lectures.length + 1,
      title: lectureTitle,
      duration: lectureDuration,
      url: lectureUrl,
      previewFree: lecturePreviewFree,
    };

    setLectures([...lectures, newLecture]);
    // Clear the lecture fields
    setLectureTitle("");
    setLectureDuration("");
    setLectureUrl("");
    setLecturePreviewFree(false);
    // Close the modal
    setShowLectureModal(false);
  };

  // Publish the new course
  const handleAddCourse = () => {
    if (!courseTitle || !courseDescription) {
      alert("Please fill in the course title and description!");
      return;
    }

    const newCourse = {
      id: courses.length + 1,
      title: courseTitle,
      description: courseDescription,
      image: thumbnailFile ? URL.createObjectURL(thumbnailFile) : "/images/default.png",
      // Hard-code these or set them dynamically if you wish
      students: 0,
      // Example: today's date as the "Published On"
      publishedDate: new Date().toLocaleDateString(),
    };

    setCourses([...courses, newCourse]);
    navigate("/my-courses");
  };

  return (
    <div className="add-course-container">
      <h2>Add Course</h2>

      {/* Course Title */}
      <div className="input-group">
        <label>Course Title</label>
        <input
          type="text"
          value={courseTitle}
          onChange={(e) => setCourseTitle(e.target.value)}
          placeholder="Enter course title"
        />
      </div>

      {/* Course Description */}
      <div className="input-group">
        <label>Course Description</label>
        <textarea
          value={courseDescription}
          onChange={(e) => setCourseDescription(e.target.value)}
          placeholder="Enter course description"
        />
      </div>

      {/* Course Thumbnail */}
      <div className="input-group">
        <label>Course Thumbnail</label>
        <input
          type="file"
          onChange={(e) => setThumbnail(e.target.files[0])}
        />
      </div>

      {/* Lectures Section */}
      <div className="lecture-section">
        <h3>Lectures</h3>
        {lectures.length > 0 ? (
          <ul className="lecture-list">
            {lectures.map((lecture) => (
              <li key={lecture.id}>
                <strong>{lecture.title}</strong> ({lecture.duration} minutes)
                {lecture.previewFree && " [Preview Free]"}
                <br />
                <small>{lecture.url}</small>
              </li>
            ))}
          </ul>
        ) : (
          <p>No lectures added yet.</p>
        )}
        <button
          onClick={() => setShowLectureModal(true)}
          className="add-lecture-btn"
        >
          + Add Lecture
        </button>
      </div>

      {/* Publish Course */}
      <button onClick={handleAddCourse} className="add-btn">
        ADD
      </button>

      {/* Lecture Modal */}
      {showLectureModal && (
        <div className="lecture-modal">
          <div className="lecture-modal-content">
            <div className="lecture-modal-header">
              <h3>Add Lecture</h3>
              <button
                className="close-modal"
                onClick={() => setShowLectureModal(false)}
              >
                &times;
              </button>
            </div>

            <div className="input-group">
              <label>Lecture Title</label>
              <input
                type="text"
                value={lectureTitle}
                onChange={(e) => setLectureTitle(e.target.value)}
                placeholder="Enter lecture title"
              />
            </div>

            <div className="input-group">
              <label>Duration (minutes)</label>
              <input
                type="text"
                value={lectureDuration}
                onChange={(e) => setLectureDuration(e.target.value)}
                placeholder="Enter lecture duration"
              />
            </div>

            <div className="input-group">
              <label>Lecture URL</label>
              <input
                type="text"
                value={lectureUrl}
                onChange={(e) => setLectureUrl(e.target.value)}
                placeholder="Enter lecture URL"
              />
            </div>

            <div className="input-group checkbox-group">
              <label>Preview Free?</label>
              <input
                type="checkbox"
                checked={lecturePreviewFree}
                onChange={(e) => setLecturePreviewFree(e.target.checked)}
              />
            </div>

            <div className="modal-buttons">
              <button onClick={handleAddLecture} className="modal-add-btn">
                Add
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AddCourse;
