import React, { useState } from "react";
import './Courses.css'

const Courses = () => {
  const user = JSON.parse(localStorage.getItem("user"));
  const [courses, setCourses] = useState([
    { id: 1, title: "React Basics", description: "Learn React from scratch" 

    },
    { id: 2, title: "Advanced JavaScript", description: "Deep dive into JS" },
    { id: 3, title: "Node.js Fundamentals", description: "Backend with Node.js" },
    { id: 4, title: "Advanced JavaScript", description: "Advanced CSS technique" }
  ]);

  const handleAddCourse = () => {
    const newCourse = { id: Date.now(), title: "New Course", description: "Course description" };
    setCourses([...courses, newCourse]);
  };

  const handleDeleteCourse = (id) => {
    setCourses(courses.filter(course => course.id !== id));
  };

  return (
    <div className="courses-container">
      <h1 className="title"> Our Courses</h1>
      {user && user.role === "admin" && (
        <button className="add-course-btn" onClick={handleAddCourse}>Add Course</button>
      )}

    <ul className="course-list">
        {courses.map((course) => (
          <li key={course.id}  className="course-card">
            <h3 className="course-title">{course.title}</h3>

            <p className="course-description">{course.description}</p>

            <button className="view-course-btn">View Course</button>

            {user && (user.role === "admin" || user.role === "instructor") && (

              <button className="edit-course-btn">Edit Course</button>
            )}
            {user && user.role === "admin" && (

              <button  className="delete-course-btn" 
               onClick={() => handleDeleteCourse(course.id)}>Delete Course</button>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Courses;
