import { useParams } from "react-router-dom";
import { useState } from "react";
import './CourseDetails.css';

//import images
import course1 from '../../assets/react.jpg';
import course2 from'../../assets/javascript.jpg';
import course3 from'../../assets/node.jpg';
import course4 from'../../assets/css.jpg';
import course5 from '../../assets/python.jpg';
import course6 from '../../assets/algor.png';
import course7 from '../../assets/ccc.jpg';
import course8 from '../../assets/iuu.jpg'

// Sample courses with full structure
const courses = [
  { 
    id: 1, 
    title: "React Basics", 
    description: "Learn React from scratch.", 
    image: course1, 
    modules: [
      { title: "Introduction to React", lectures: [
          { title: "What is React?", duration: "10 minutes", video: "https://www.youtube.com/embed/s2skans2dP4?si=U6BQLKMBYebujLrj" },
          { title: "JSX and Components", duration: "12 minutes", video: "/videos/react2.mp4" }
      ]},
     
    ]
  },
  { 
    id: 2, 
    title: "Advanced JavaScript", 
    description: "Deep dive into JavaScript concepts.", 
    image: course2, 
    modules: [
      { title: "ES6 Features", lectures: [
          { title: "Arrow Functions", duration: "8 minutes", video: "/videos/js1.mp4" },
          { title: "Destructuring & Spread", duration: "10 minutes", video: "/videos/js2.mp4" }
      ]},
    
    ]
  },
  { 
    id: 3, 
    title: "Node.js Fundamentals", 
    description: "Backend development with Node.js", 
    image: course3, 
    modules: [
      { title: "Introduction to Node.js", lectures: [
          { title: "What is Node.js?", duration: "10 minutes", video: "/videos/node1.mp4" },
          { title: "Setting up a Server", duration: "12 minutes", video: "/videos/node2.mp4" }
      ]},
    
    ]
  },
  { 
    id: 4, 
    title: "CSS Mastery", 
    description: "Advanced CSS techniques.", 
    image: course4, 
    modules: [
     
      { title: "CSS Animations", lectures: [
          { title: "Keyframes & Transitions", duration: "14 minutes", video: "/videos/css3.mp4" },
          { title: "Animating Elements", duration: "16 minutes", video: "/videos/css4.mp4" }
      ]},
    ]
  },
  { 
    id: 5, 
    title: "Python Programming", 
    description: "Learn Python from the basics.", 
    image: course5, 
    modules: [
      { title: "Getting Started with Python", lectures: [
          { title: "Syntax & Variables", duration: "10 minutes", video: "/videos/python1.mp4" },
          { title: "Loops & Functions", duration: "12 minutes", video: "/videos/python2.mp4" }
      ]},
    
    ]
  },
  { 
    id: 6, 
    title: "Algorithms", 
    description: "Algorithm design and analysis.", 
    image: course6, 
    modules: [
      { title: "Sorting Algorithms", lectures: [
          { title: "Bubble & Selection Sort", duration: "10 minutes", video: "/videos/algor1.mp4" },
          { title: "Merge & Quick Sort", duration: "12 minutes", video: "/videos/algor2.mp4" }
      ]},
     
    ]
  },
  { 
    id: 7, 
    title: "Advanced C++ Course", 
    description: "Master memory management, templates, and concurrency.", 
    image: course7, 
    modules: [
      { title: "Memory Management", lectures: [
          { title: "Pointers and References", duration: "12 minutes", video: "/videos/cpp1.mp4" },
          { title: "Dynamic Allocation", duration: "14 minutes", video: "/videos/cpp2.mp4" }
      ]},
      
    ]
  },
  { 
    id: 8, 
    title: "UI/UX Design", 
    description: "Design beautiful and user-friendly interfaces.", 
    image: course8, 
    modules: [
      { title: "UI Principles", lectures: [
          { title: "Color Theory & Typography", duration: "12 minutes", video: "/videos/ui1.mp4" },
          { title: "Designing for Accessibility", duration: "14 minutes", video: "/videos/ui2.mp4" }
      ]},
      
    ]
  }
];

const CourseDetails = () => {
  const { id } = useParams();
  const course = courses.find((course) => course.id === parseInt(id));
  const [expandedModule, setExpandedModule] = useState(null);

  if (!course) {
    return <h2>Course not found</h2>;
  }

  return (
    <div className="course-details">
      <div className="course-header">
        <div className="course-info">
          <h2>{course.title}</h2>
          <p>{course.description}</p>
        </div>
    
        <img src={course.image} alt={course.title} className="course-image" />
       </div>


      {/* Course Structure */}
      <div className="course-box">
      <h3 className="section-title">Course Structure</h3>
      <div className="course-structure">
        {course.modules.map((module, index) => (
          <div key={index} className="module">
            <h4 
              className={`module-title ${expandedModule === index ? "expanded" : ""}`} 
              onClick={() => setExpandedModule(expandedModule === index ? null : index)}
            >
              {module.title} - {module.lectures.length} lectures - {module.duration}
            </h4>
            {expandedModule === index && (
              <div className="lectures">
                {module.lectures.map((lecture, idx) => (
                  <div key={idx} className="lecture">
                    <span className="lecture-title">{lecture.title}</span>
                    <span className="lecture-duration">{lecture.duration}</span>
                    <a href={lecture.video} className="watch-link">Watch</a>
                  </div>
                
                ))}
              </div>
            )}
          </div>
        ))}
      </div>

      {/* Rating Section */}
      <div className="rating">
        <h4>Rate this Course:</h4>
        ⭐⭐⭐⭐⭐
      </div>
    </div>
    </div>
  );
};




export default CourseDetails;
