import React, { useState } from "react";
import './AddCourses.css'

const AddCourse = () => {
  const [courseTitle, setCourseTitle] = useState("");
  const [courseDescription, setCourseDescription] = useState("");
  const [coursePrice, setCoursePrice] = useState(0);
  const [discount, setDiscount] = useState(0);
  const [thumbnail, setThumbnail] = useState(null);

  const handleThumbnailUpload = (e) => {
    const file = e.target.files[0];
    setThumbnail(file);
  };

  const handleAddCourse = () => {
    const newCourse = {
      title: courseTitle,
      description: courseDescription,
      price: coursePrice,
      discount: discount,
      thumbnail: thumbnail ? URL.createObjectURL(thumbnail) : "",
    };
    console.log("New Course Added:", newCourse);
    alert("Course Added Successfully!");
  };

  return (
    <div className="add-course-container">
      <h2>Add Course</h2>

      <label>Course Title</label>
      <input
        type="text"
        value={courseTitle}
        onChange={(e) => setCourseTitle(e.target.value)}
        placeholder="Enter course title"
      />

      <label>Course Description</label>
      <textarea
        value={courseDescription}
        onChange={(e) => setCourseDescription(e.target.value)}
        placeholder="Enter course description"
      ></textarea>

      <label>Course Price</label>
      <input
        type="number"
        value={coursePrice}
        onChange={(e) => setCoursePrice(e.target.value)}
      />

      <label>Discount %</label>
      <select value={discount} onChange={(e) => setDiscount(e.target.value)}>
        {Array.from({ length: 100 }, (_, i) => (
          <option key={i} value={i}>
            {i}%
          </option>
        ))}
      </select>

      <label>Course Thumbnail</label>
      <input type="file" onChange={handleThumbnailUpload} />

      <button className="add-btn" onClick={handleAddCourse}>ADD</button>
    </div>
  );
};

export default AddCourse;
