import React, { useState } from "react";

const Courses = () => {
  const user = JSON.parse(localStorage.getItem("user"));
  const [courses, setCourses] = useState([
    { id: 1, title: "React Basics", description: "Learn React from scratch" },
    { id: 2, title: "Advanced JavaScript", description: "Deep dive into JS" }
  ]);

  const handleAddCourse = () => {
    const newCourse = { id: Date.now(), title: "New Course", description: "Course description" };
    setCourses([...courses, newCourse]);
  };

  const handleDeleteCourse = (id) => {
    setCourses(courses.filter(course => course.id !== id));
  };

  return (
    <div className="min-h-screen bg-gray-100 py-10">
      <div className="max-w-6xl mx-auto px-4">
        <h1 className="text-3xl font-bold text-gray-900 mb-6">Courses</h1>

        {user && user.role === "admin" && (
          <button
            onClick={handleAddCourse}
            className="mb-6 px-6 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 transition duration-300"
          >
            Add Course
          </button>
        )}

        <ul className="space-y-6">
          {courses.map((course) => (
            <li
              key={course.id}
              className="bg-white p-6 rounded-lg shadow-md hover:shadow-lg transition duration-300"
            >
              <h3 className="text-2xl font-semibold text-gray-800">{course.title}</h3>
              <p className="text-gray-600 mb-4">{course.description}</p>

              <div className="space-x-4">
                <button className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition duration-300">
                  View Course
                </button>

                {user && (user.role === "admin" || user.role === "instructor") && (
                  <button className="px-4 py-2 bg-yellow-500 text-white rounded-lg hover:bg-yellow-600 transition duration-300">
                    Edit Course
                  </button>
                )}

                {user && user.role === "admin" && (
                  <button
                    onClick={() => handleDeleteCourse(course.id)}
                    className="px-4 py-2 bg-red-500 text-white rounded-lg hover:bg-red-600 transition duration-300"
                  >
                    Delete Course
                  </button>
                )}
              </div>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Courses;
