import React from 'react';
import { Link } from "react-router-dom";

const Home = () => {
  return (
    <div className="home"> 
      <div className="home-image">
        <img src="" alt="" />
      </div>
      <div className="home-content"></div>
      <h1  className="home-header">Welcome to Bloom Academy</h1>
      <p className="home-description">An online learning platform to boost your skills</p>
      <Link to="/courses" className="explore-btn"><button className='explore-btn'>Explore Courses </button></Link>
    </div>
  );
};

export default Home;




<div className="home">
  <div className="home-content">
    <h1 className="home-header">Welcome to Our Platform</h1>
    <p className="home-description">
      This is a place where you can learn and grow in various fields of technology.
    </p>
    <button className="explore-btn">Explore</button>
  </div>
  
  <div className="home-image">
    <img src="your-image-url-here" alt="Home Image" className="home-logo" />
  </div>
</div>
