import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./AddCourses.css"; // For styling

const AddCourse = ({ courses, setCourses }) => {
  const navigate = useNavigate();

  // Course fields
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [thumbnailFile, setThumbnailFile] = useState(null);

  const handleAddCourse = () => {
    if (!title || !description) {
      alert("Please fill in all required fields!");
      return;
    }

    // Build a new course object
    const newCourse = {
      id: courses.length + 1,
      title: title,
      description: description,
      // Convert uploaded file to a local URL
      image: thumbnailFile ? URL.createObjectURL(thumbnailFile) : "/images/default.png",
      // Hard-code these or set them dynamically if you wish

      students: 0,
      // Example: today's date as the "Published On"
      publishedDate: new Date().toLocaleDateString(),
    };

    // Append to the existing list of courses
    setCourses([...courses, newCourse]);

    // Navigate to My Courses
    navigate("/my-courses");
  };

  return (
    <div className="add-course-container">
      <h2>Add Course</h2>

      <div className="input-group">
        <label>Course Title</label>
        <input
          type="text"
          placeholder="Enter course title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
      </div>

      <div className="input-group">
        <label>Course Description</label>
        <textarea
          placeholder="Enter course description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
      </div>

      <div className="input-group">
        <label>Course Thumbnail</label>
        <input
          type="file"
          onChange={(e) => setThumbnailFile(e.target.files[0])}
        />
      </div>

      <button onClick={handleAddCourse} className="add-btn">
        ADD
      </button>
    </div>
  );
};

export default AddCourse;
