import React from 'react';
import { Link } from "react-router-dom";
import './Home.css'
import home_img from '../../assets/home.jpg'

const Home = () => {
  return (
    <div className='home-container'>
      <img src={home_img} alt="" className='home-logo'/>
      <h1 className='home-h1'>Welcome to Bloom Academy</h1>
      <p className='home-p'>An online learning platform to boost your skills</p>
      <Link to="/courses" className='explore-btn'>Explore Courses</Link>
    </div>
  );
};

export default Home;
